//SPDX-License-Identifier: MIT
pragma solidity ^0.8.4;

contract Gambling {
  address public randomNumberGenerator;

  constructor() {
        randomNumberGenerator = msg.sender;
  }

event Good(uint number, uint gamesPlayed);
event Winner(address winner);


  mapping(address => uint) gamesPlayed;

 function playGame(uint randomNumber)public {
    // require(msg.sender == randomNumberGenerator);
    gamesPlayed[msg.sender]++;
    emit Good(randomNumber, gamesPlayed[msg.sender]);
    emit Winner(msg.sender);


 }


}

